import "./Booking.css";
import { DateRange } from "react-date-range";
import Navbar from "../../components/navbar/Navbar";
import { useLocation, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

const Booking = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const [date, setDate] = useState([
    {
      startDate: location.state.date[0].startDate,
      endDate: location.state.date[0].endDate,
      key: "selection",
    },
  ]);
  const [info, setInfo] = useState({
    fullName: "",
    email: "",
    phoneNumber: "",
    idNumber: "",
  });

  const user = location.state.user;
  const hotel = location.state.hotel;
  const options = location.state.options;

  console.log(options);

  useEffect(() => {
    const roomId = {
      id: hotel.rooms,
      options: options,
    };

    const postRoom = () => {
      fetch("http://localhost:5000/room", {
        method: "POST",
        body: JSON.stringify(roomId),
        headers: { "Content-Type": "application/json" },
      })
        .then((res) => res.json())
        .then((data) => {
          console.log(data);
        })
        .catch((err) => console.log(err));
    };

    postRoom();
  }, []);
  return (
    <div>
      <Navbar />
      <div className="wrapper">
        <div className="hotel">
          <div className="hotel__details">
            <h1>{hotel.name}</h1>
            <p>{hotel.desc}</p>
          </div>
          <div className="hotel_booking">
            <div className="box">
              <h1>${hotel.cheapestPrice} (1 night)</h1>
              <button className="upper-button">Reserve or Book Now!</button>
            </div>
          </div>
        </div>
        <div className="date__info">
          {" "}
          <div className="date__info-date">
            <h1>Dates</h1>
            <DateRange
              onChange={(item) => setDate([item.selection])}
              showSelectionPreview={true}
              ranges={date}
              direction="horizontal"
            />
          </div>
          <div className="date__info-info">
            <h1>Reserve Info</h1>
            <form action="">
              <div className="date__info-info-input">
                <label>Your Full Name:</label>
                <input
                  type="text"
                  placeholder="Full Name"
                  name="fullName"
                  onChange={(e) =>
                    setInfo({ ...info, fullName: e.target.value })
                  }
                />
              </div>
              <div className="date__info-info-input">
                <label>Your Email:</label>
                <input
                  type="text"
                  placeholder="Email"
                  name="email"
                  onChange={(e) => setInfo({ ...info, email: e.target.value })}
                />
              </div>
              <div className="date__info-info-input">
                <label>Your Phone Number:</label>
                <input
                  type="text"
                  placeholder="Phone Number"
                  name="phone"
                  onChange={(e) =>
                    setInfo({ ...info, phoneNumber: e.target.value })
                  }
                />
              </div>
              <div className="date__info-info-input">
                <label>Your Indentity Card Number:</label>
                <input
                  type="text"
                  placeholder="Indentity Card Number"
                  name="idNumber"
                  onChange={(e) =>
                    setInfo({ ...info, idNumber: e.target.value })
                  }
                />
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Booking;
